import CONSTANTS from "./constants";

export async function fetchApi(endPoint: RequestInfo, options: RequestInit):Promise<any> {
    return new Promise((resolve, reject) => {

        const timeout = new Promise((resolve, reject) => {
            setTimeout(() => {
                reject(null);
            }, 5000);
        });
        return Promise.race([timeout, fetch(`${CONSTANTS.URL}/${endPoint}`, options)
            .then(response => response.json().then(res => {

                return resolve(res)
            })).catch(e => reject(null))]);


    })
}
