    const [inputErrors, setInputErrors] = useState({});

    function handleInputChange(event: React.ChangeEvent<HTMLInputElement>) {
        setState({[event.currentTarget.id]: event.currentTarget.value})
        setInputErrors({[event.currentTarget.id]:handleFormValidation(event.currentTarget.type, event.currentTarget.value, event.currentTarget.required)})
    }

    function submitForm(event:React.SyntheticEvent<HTMLFormElement>) {
        event.preventDefault();

        console.log(event.currentTarget.checkValidity());

    }

