import React, { useEffect, useState, useRef } from 'react';
import { getApi } from '../../utils/functions';
import { IHit, INews } from '../../utils/interfaces';



function LandingPage() {
    let live = true;
    const [news, setNews] = useState<IHit[]>();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>();
    const [searchString, setSearchString] = useState("");
    const searchInputRef = useRef();

    useEffect(() => {
        if (live) {
            FetchNews();
        }
        return () => {
            live = false;
        }
    }, [])


    async function FetchNews(searchValue?: string) {
        const requestOptions: RequestInit = {
            method: "GET",
            redirect: "follow",

        };
        let response: INews | null;

        if (searchValue) {
            response = await getApi(`query=${searchValue}`, requestOptions)
        } else {
            response = await getApi(`query=microfrontend`, requestOptions)
        }

        console.log("FetchNews : ", response);

        if (response) {
            setLoading(false);
            setNews(response.hits);
        } else {
            setLoading(false);
            setError("Failed To Fetch");
        }
    }



    function handleSearchInput(event: React.ChangeEvent<HTMLInputElement>) {
        setSearchString(event.currentTarget.value);
    }

    function submitQuery(e: any) {
        e.preventDefault();
        if (searchString) {
            setLoading(true);
            FetchNews(searchString);
        }
    }

    function clearInput() {
        setSearchString('');
    }

    return (
        <div className="container w-screen h-screen p-4 bg-purple-100 shadow-lg rounded max-h-screen overflow-y-hidden overflow-x-hidden">
            <img className="float-right h-12" src="https://icon.now.sh/react/c0c" alt="React Logo" />
            <h2 className="text-grey-darkest font-thin">Developer News</h2>
            <form className="mb-2" onSubmit={submitQuery}>
                <input className="border p-1 rounded" value={searchString} type="text" name="search" id="search" onChange={e => handleSearchInput(e)} />
                <button className="bg-orange-500 rounded m-1 p-1" type="submit">Search</button>
                <button className="bg-teal-500 m-1 text-white rounded p-1" onClick={clearInput} type='button'>clear input</button>
            </form>
        <div className='flex-grow-1 w-screen'>

            {loading ? <div className=" font-bold text-orange-500 ">fetching results....</div> : <div className='overflow-y-scroll max-h-screen'>{news ? news.map((entry, i) => {
                console.log("news.map((entry,i) === ", entry.story_title);

                let NewsEntryComponent = null;
                if (entry.title) {
                    NewsEntryComponent = <ul className="list-reset leading-normal " style={{ display: 'flex', flexDirection: 'column', marginBottom: 8 }} key={entry.objectID}>
                        <li>
                            <div className="max-w-sm w-full lg:max-w-full lg:flex">
                                <div className="h-48 lg:h-auto lg:w-48 flex-none bg-cover rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden"  title="Woman holding a mug">
                                </div>
                                <div className="border-r border-b border-l border-gray-400 lg:border-l-0 lg:border-t lg:border-gray-400 bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
                                    <div className="mb-8">
                                        <p className="text-sm text-gray-600 flex items-center">
                                            <svg className="fill-current text-gray-500 w-3 h-3 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                                                <path d="M4 8V6a6 6 0 1 1 12 0v2h1a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2v-8c0-1.1.9-2 2-2h1zm5 6.73V17h2v-2.27a2 2 0 1 0-2 0zM7 6v2h6V6a3 3 0 0 0-6 0z" />
                                            </svg>
                                            Members only
                                        </p>
                                        <div className="text-gray-900 font-bold text-xl mb-2">Can coffee make you a better developer?</div>
                                        <p className="text-gray-700 text-base">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.</p>
                                    </div>
                                    <div className="flex items-center">
                                        <img className="w-10 h-10 rounded-full mr-4" src="/img/jonathan.jpg" alt="Avatar of Jonathan Reinink"/>
                                            <div className="text-sm">
                                                <p className="text-gray-900 leading-none">Jonathan Reinink</p>
                                                <p className="text-gray-600">Aug 18</p>
                                            </div>
                                    </div>
                                </div>
                            </div>
                            <a className="text-indigo-600 rk hover:text-indigo-900" href={entry.url}> {`${i + 1}-${entry.title}`}</a>
                        </li>

                    </ul>
                }

                return NewsEntryComponent;
            }) : null}</div>
            }
        </div>
            <div className="text-red-500 font-bold">{error ? error : null}</div>
        </div>
    );
}

export default LandingPage;
