import { IAuth, IUser } from "./interfaces";
import CONSTANTS from './constants';
import { getNewsApi, getAuthApi, postAuthApi } from './functions';
const jwt = require('jsonwebtoken');

export const createUser = ({email, password}:IAuth)=>{
    const token = jwt.sign({
        email,
        password
    }, CONSTANTS.SALT, {
        expiresIn: 60 * 60 * 24 * 7 // Expires in a week
    })
    console.log(token);
    return token
}

export const  signIn = ({email, password}:IAuth)=>{
    const headers = new Headers();
    headers.append("Content-Type","application/json")
    const requestOptions: RequestInit = {
        method: "GET",
        redirect: "follow",
        headers: headers
    };
    let response: IUser | null= postAuthApi('auth/login', requestOptions)



    return response
}
