import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import { IUserState } from '../../../utils/interfaces'
import { setRegister, login } from './user-actions'

// Define a type for the slice state


// Define the initial state using that type
const initialState: IUserState = {
  email: null,
  name: null,
  dob: null,
  token: undefined,
  loggedIn: false,
  isLoading: false
}

export const user = createSlice({
  name: 'user',
  // `createSlice` will infer the state type from the `initialState` argument
  initialState:initialState,
  reducers: {
    logOut: (state) => {
      state = {
        email: null,
        name: null,
        dob: null,
        token: undefined,
        loggedIn: false,
        isLoading: false,
      }
    }

  },
  extraReducers: builder =>{
    builder.addCase(setRegister.pending,(state)=>{
      state.isLoading = true
    })
    builder.addCase(setRegister.rejected,(state,{payload})=>{
      state.loggedIn = false
      state.token= undefined
      state.isLoading = false
    })
    builder.addCase(setRegister.fulfilled,(state,{payload})=>{

    })
    builder.addCase(login.pending,(state)=>{
      state.isLoading = true
    })
    builder.addCase(login.rejected,(state,{payload})=>{
      state.loggedIn = false
      state.token= undefined
      state.isLoading = false
    })
    builder.addCase(login.fulfilled,(state,{payload})=>{
      state.loggedIn = true
      state.token= payload
      state.isLoading = false
    })
  }
})

export const { logOut } = user.actions;


export default user.reducer;
