import { IAuth, IUser } from "./interfaces";
import CONSTANTS from './constants';
import { getNewsApi, getAuthApi } from './functions';
const jwt = require('jsonwebtoken');

export const createUser = ({email, password}:IAuth)=>{
    const token = jwt.sign({
        email,
        password
    }, CONSTANTS.SALT, {
        expiresIn: 60 * 60 * 24 * 7 // Expires in a week
    })
    console.log(token);
    return token
}

export const  signIn = ({email, password}:IAuth)=>{
    const requestOptions: RequestInit = {
        method: "GET",
        redirect: "follow",

    };
    let response: IUser | null= getAuthApi('auth/login', requestOptions)



    return response
}
