import CONSTANTS from "./constants";

export function fetchApi(endPoint: RequestInfo, options: RequestInit): any {
    return new Promise((resolve, reject) => {

        const timeout = new Promise((resolve, reject) => {
            setTimeout(() => {
                reject(null);
            }, 5000);
        });
        return Promise.resolve(fetch(`${CONSTANTS.URL}?${endPoint}`, options)
            .then(response => response.json().then(res => {
                console.log("fetchApi res: ", res);

                return resolve(res)
            })).catch(e => reject(null)));


    })
}
