import React, { useEffect, useState, useRef } from 'react';
import { getApi } from '../../utils/functions';
import { IHit, INews } from '../../utils/interfaces';



function LandingPage() {
    let live = true;
    const [news, setNews] = useState<IHit[]>();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>();
    const [searchString, setSearchString] = useState("");
    const searchInputRef = useRef();

    useEffect(() => {
        if (live) {
            FetchNews();
        }
        return () => {
            live = false;
        }
    }, [])


    async function FetchNews(searchValue?: string) {
        const requestOptions: RequestInit = {
            method: "GET",
            redirect: "follow",

        };
        let response: INews | null;

        if (searchValue) {
            response = await getApi(`query=${searchValue}`, requestOptions)
        } else {
            response = await getApi(`query=microfrontend`, requestOptions)
        }

        console.log("FetchNews : ", response);

        if (response) {
            setLoading(false);
            setNews(response.hits);
        } else {
            setLoading(false);
            setError("Failed To Fetch");
        }
    }



    function handleSearchInput(event: React.ChangeEvent<HTMLInputElement>) {
        setSearchString(event.currentTarget.value);
    }

    function submitQuery(e: any) {
        e.preventDefault();
        if (searchString) {
            setLoading(true);
            FetchNews(searchString);
        }
    }

    function clearInput() {
        setSearchString('');
    }

    return (
        <div style={{ maxHeight: "100vh" }} className="container w-screen h-screen p-4 bg-purple-100 shadow-lg rounded  overflow-x-hidden ">
            <img className="float-right h-12" src="https://icon.now.sh/react/c0c" alt="React Logo" />
            <h2 className="text-grey-darkest font-thin">Developer News</h2>
            <form className="mb-2" onSubmit={submitQuery}>
                <input className="border p-1 rounded" value={searchString} type="text" name="search" id="search" onChange={e => handleSearchInput(e)} />
                <button className="bg-orange-500 rounded m-1 p-1" type="submit">Search</button>
                <button className="bg-teal-500 m-1 text-white rounded p-1" onClick={clearInput} type='button'>clear input</button>
            </form>
            <div className=''>

                {loading ? <div className=" font-bold text-orange-500 ">fetching results....</div> : <div className=' overflow-y-scroll'>
                    <ul >
                        {news ? news.map((entry, i) => {

                            return <li key={entry.objectID}>
                                <div className=" mb-4">
                                    {/* <pre>{JSON.stringify(entry, null, '\t')}</pre> */}
                                    <div className=" hover:text-indigo-900 border-r border-b border-l border-gray-400 lg:border-l-0 lg:border-t lg:border-gray-400 bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
                                        <div className="mb-2">

                                            <div className="text-gray-900 font-bold text-xl mb-2">

                                                {(entry.url || entry.story_url) ? <div >
                                                    <h6>
                                                        {entry.title}{entry.story_title}
                                                    </h6>
                                                    <small className=''>
                                                        {(entry.url || entry.story_url) ? <a className='text-grey-400 font-light' href={(entry.url || entry.story_url)}>
                                                            {(entry.url || entry.story_url)}
                                                        </a> : null}
                                                    </small>

                                                </div> : null}

                                            </div>
                                            <p className="text-gray-700 text-base">{entry.story_text}</p>
                                        </div>
                                        <div className="flex items-center">
                                            {/* <img className="w-10 h-10 rounded-full mr-4" src="/img/jonathan.jpg" alt="Avatar of Jonathan Reinink" /> */}
                                            <div className="text-sm">
                                                <p className="text-gray-900 leading-none">{entry.author}</p>
                                                <p className="text-gray-600">{entry.created_at}</p>
                                            </div>
                                        </div>
                                    </div>
                                    {/* <a className="text-indigo-600 rk hover:text-indigo-900" href={entry.url}> {`${i + 1}-${entry.title}`}</a> */}
                                </div>
                            </li>
                        }) : null}
                    </ul>
                </div>
                }
            </div>
            <div className="text-red-500 font-bold">{error ? error : null}</div>
        </div>
    );
}

export default LandingPage;
