import axios from "axios";
import CONSTANTS from "./constants";

export function fetchApi(endPoint: RequestInfo, options: RequestInit): any {
    return new Promise(async(resolve, reject) => {
        axios.get(`${CONSTANTS.URL}?${endPoint}`).then(response=>{

            resolve(response.data)
        }).catch(e=>{

            reject(null)
        })



    })
}
